
def average(a, b, c):
    x = (a + b + c) / 3
    return x


def add(x, y):
    return x + y

def subtract(x, y):
    return x - y

def multiply(x, y):
    return x * y

def divide(x, y):
    return x / y

def square(x):
    return x * x

def cube(x):
    return x * x * x

def power(x, y):
    return x ** y

def mod(x, y):
    return x % y

def odd_even(x):
    if x % 2 == 0:
        return "Even"
    else:
        return "Odd"