def LabelSelect(
    *option,  # Options for the select dropdown (can use `Options` helper function to create)
    label=(),  # String or HtmlString component for the label
    lbl_cls="",  # Additional classes for the label
    inp_cls="",  # Additional classes for the select input
    cls=cn(
        "space-y-2",
    ),  # Classes for the outer div
    id="",  # ID for the select input
    name="",  # Name attribute for the select input
    placeholder="",  # Placeholder text for the select input
    searchable=False,  # Whether the select should be searchable
    select_kwargs=None,  # Additional Arguments passed to Select
    **kwargs,
):  # Additional arguments passed to Select
    "A FormLabel and Select pair that provides default spacing and links/names them based on id"
    lbl_cls, inp_cls, cls = map(cn, (lbl_cls, inp_cls, cls))
    select_kwargs = ifnone(select_kwargs, {})
    if label:
        lbl = FormLabel(label,cls=f"{lbl_cls}", fr=id)
    select = Select(
        *option,
        inp_cls=inp_cls,
        id=id,
        name=name if name else id,
        placeholder=placeholder,
        searchable=searchable,
        select_kwargs=select_kwargs,
        **kwargs,
    )
    return Div(lbl, select,cls=cls) if label else Div(select,cls=cls)


