def Card(
    *c,  # Components that go in the body (Main content of the card such as a form, and image, a signin form, etc.)
    header: HtmlString
    | Iterable[
        HtmlString
    ] = None,  # Component(s) that goes in the header (often a `ModalTitle` and a subtitle)
    footer: HtmlString
    | Iterable[
        HtmlString
    ] = None,  # Component(s) that goes in the footer (often a `ModalCloseButton`)
    body_cls="space-y-6",  # classes for the body
    header_cls="",  # classes for the header
    footer_cls="",  # classes for the footer
    cls="",  # class for outermost component
    **kwargs,  # additional arguments for the `CardContainer`
) -> HtmlString:  # Card component
    "Creates a Card with a header, body, and footer"
    header_cls, footer_cls, body_cls, cls = map(
        cn, (header_cls, footer_cls, body_cls, cls)
    )
    res = []
    if header:
        res.append(CardHeader(header,cls=header_cls))
    res.append(CardBody(*c,cls=body_cls))
    if footer:
        res.append(CardFooter(footer,cls=footer_cls))
    return CardContainer(*res,cls=cls, **kwargs)


