def _TableCell(
    Component,
    *c,  # Components that go in the cell
    cls="",  # Additional classes on the cell container
    shrink=False,  # Whether to shrink the cell
    expand=False,  # Whether to expand the cell
    small=False,  # Whether to use a small table
    **kwargs,  # Additional args for the cell
) -> HtmlString:  # Table cell
    "Creates a table cell"
    cls = cn(cls)
    if shrink:
        cls += " uk-table-shrink"
    if expand:
        cls += " uk-table-expand"
    if small:
        cls += " uk-table-small"
    return Component(*c, cls=cls, **kwargs)


@delegates(_TableCell, but=["Component"])
def Td(*c, **kwargs):
    return _TableCell(fh.Td, *c, **kwargs)


@delegates(_TableCell, but=["Component"])
def Th(*c, **kwargs):
    return _TableCell(fh.Th, *c, **kwargs)


def Tbody(*rows, cls="", sortable=False, **kwargs):
    return fh.Tbody(*rows, cls=cn(cls), uk_sortable=sortable, **kwargs)


