def TableFromLists(
    header_data: Sequence,  # List of header data
    body_data: Sequence[Sequence],  # List of lists of body data
    footer_data=None,  # List of footer data
    header_cell_render=Th,  # Function(content) -> HtmlString that renders header cells
    body_cell_render=Td,  # Function(key, content) -> HtmlString that renders body cells
    footer_cell_render=Td,  #  Function(key, content) -> HtmlString that renders footer cells
    cls=cn(
        TableT.middle,
        TableT.divider,
        TableT.hover,
        TableT.sm,
    ),  # Additional classes on the table
    sortable=False,  # Whether to use sortable table
    **kwargs,  # Additional args for the table
) -> HtmlString:  # Table from lists
    "Creates a Table from a list of header data and a list of lists of body data"
    return Table(
        Thead(Tr(*map(header_cell_render, header_data))),
        Tbody(*[Tr(*map(body_cell_render, r)) for r in body_data], sortable=sortable),
        Tfoot(Tr(*map(footer_cell_render, footer_data))) if footer_data else "",
        cls=cn(cls),
        **kwargs,
    )


# %% ../nbs/02_franken.ipynb
def TableFromDicts(
    header_data: Sequence,  # List of header data
    body_data: Sequence[dict],  # List of dicts of body data
    footer_data=None,  # List of footer data
    header_cell_render=Th,  # Function(content) -> HtmlString that renders header cells
    body_cell_render=lambda k, v: Td(
        v
    ),  # Function(key, content) -> HtmlString that renders body cells
    footer_cell_render=lambda k, v: Td(
        v
    ),  #  Function(key, content) -> HtmlString that renders footer cells
    cls=cn(
        TableT.middle,
        TableT.divider,
        TableT.hover,
        TableT.sm,
    ),  # Additional classes on the table
    sortable=False,  # Whether to use sortable table
    **kwargs,  # Additional args for the table
) -> HtmlString:  # Styled Table
    "Creates a Table from a list of header data and a list of dicts of body data"
    return Table(
        Thead(Tr(*[header_cell_render(h) for h in header_data])),
        Tbody(
            *[
                Tr(*[body_cell_render(k, r.get(k, "")) for k in header_data])
                for r in body_data
            ],
            sortable=sortable,
        ),
        Tfoot(
            Tr(
                *[
                    footer_cell_render(k, footer_data.get(k.lower(), ""))
                    for k in header_data
                ]
            )
        )
        if footer_data
        else "",
        cls=cn(cls),
        **kwargs,
    )


