# %% ../nbs/02_franken.ipynb
spy_js = """
const slug = (s) => s.toLowerCase().trim().replace(/[^a-z0-9\s-]/g, '').replace(/\s+/g, '-').replace(/-+/g, '-');
const frag = document.createDocumentFragment();

document.querySelector('%s').querySelectorAll('%s').forEach(h => {
  const id = slug(h.textContent); h.id = id;
  const li = document.createElement('li'), a = document.createElement('a');
  const l = parseInt(h.tagName[1]) || 6;
  li.className = `[&.uk-active]:bg-[hsl(var(--primary)/0.4)] uk-rounded pl-[${(l - 1) * 0.75}rem] text-sm`;
  a.href = `#${id}`; a.textContent = h.textContent.trim();
  a.className = '!line-clamp-1';
  li.append(a); frag.append(li);
});
document.querySelector('#%s .uk-nav').append(frag);
"""


