"""
File Extension Regex Module
============================

Provides regex pattern for validating file extensions.

"""


# =============================================================================
# Imports
# =============================================================================

# Import | Standard Library
import re

# =============================================================================
# Constants
# =============================================================================

EXTENSION_REGEX: re.Pattern[str] = re.compile(
    pattern=r"^[a-z0-9][a-z0-9._-]*$",
    flags=re.IGNORECASE,
)


# =============================================================================
# Module Exports
# =============================================================================

__all__: list[str] = [
    "EXTENSION_REGEX",
]
