# Reset
RESET = "\033[0m"
BOLD = "\033[1m"
UNDERLINE = "\033[4m"
DIM = "\033[2m"  # sometimes useful alongside bold

# Regular foreground colors
BLACK = "\033[30m"
RED = "\033[31m"
GREEN = "\033[32m"
YELLOW = "\033[33m"
BLUE = "\033[34m"
MAGENTA = "\033[35m"
CYAN = "\033[36m"
WHITE = "\033[37m"

# Bold / Bright foreground colors
BOLD_BLACK = "\033[1;30m"
BOLD_RED = "\033[1;31m"
BOLD_GREEN = "\033[1;32m"
BOLD_YELLOW = "\033[1;33m"
BOLD_BLUE = "\033[1;34m"
BOLD_MAGENTA = "\033[1;35m"
BOLD_CYAN = "\033[1;36m"
BOLD_WHITE = "\033[1;37m"

# Underlined foreground colors
UNDERLINE_BLACK = "\033[4;30m"
UNDERLINE_RED = "\033[4;31m"
UNDERLINE_GREEN = "\033[4;32m"
UNDERLINE_YELLOW = "\033[4;33m"
UNDERLINE_BLUE = "\033[4;34m"
UNDERLINE_MAGENTA = "\033[4;35m"
UNDERLINE_CYAN = "\033[4;36m"
UNDERLINE_WHITE = "\033[4;37m"

# Bold + Underlined combinations (optional but often handy)
BOLD_UNDERLINE_BLACK = "\033[1;4;30m"
BOLD_UNDERLINE_RED = "\033[1;4;31m"
BOLD_UNDERLINE_GREEN = "\033[1;4;32m"
BOLD_UNDERLINE_YELLOW = "\033[1;4;33m"
BOLD_UNDERLINE_BLUE = "\033[1;4;34m"
BOLD_UNDERLINE_MAGENTA = "\033[1;4;35m"
BOLD_UNDERLINE_CYAN = "\033[1;4;36m"
BOLD_UNDERLINE_WHITE = "\033[1;4;37m"
