# Golden Test: Mixed Statement Boundaries
# Tests complex scenarios with multiple statement types including multi-parameter functions
# Critical regression: All statement boundaries must be correctly identified

let x : integer = 10
let y = 20

action calc(a: integer, b: integer) -> integer {
    return a * b
}

let z = calc(x, y)
print("Z: " + string(z))

data Point {
    x: integer
}

let point = Point(5)
print("Point: " + string(point.x))
